document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("location"),t=document.getElementById("real-temp"),n=document.getElementById("last-checked"),o=document.getElementById("feel-temp"),r=document.getElementById("humidity");fetch("https://cors-anywhere.herokuapp.com/http://reg.bom.gov.au/fwo/IDW60901/IDW60901.94608.json").then(e=>e.json()).then(d=>{const{refresh_message:a,state_time_zone:s}=d.observations.header[0],{name:c,apparent_t:i,air_temp:m,local_date_time:l,rel_hum:g}=d.observations.data[0];console.log(c,i,m,l,g),e.innerHTML=`${c}, ${s}`,t.innerHTML=`${m}°C`,o.innerHTML=`Feels Like: <strong>${i}°C</strong>`;let u=l.replace(/\d+\/0?/,"");n.innerHTML=`Accurate as of: <strong>${u}</strong>`,r.innerHTML=`Humidity: <strong>${g}%</strong>`});t.addEventListener("click",()=>{let e=t.innerHTML.includes("C"),n=Number(t.innerHTML.replace(/°\w/,"")),o=e?9*n/5+32:5*(n-32)/9;o=o.toString().replace(/(\d+\.\d)\d*/,(e,t)=>t),t.innerHTML=e?`${o}°F`:`${o}°C`})});